package exercise.week1;

public class Swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=30,b=15,temp;
		System.out.println("\nBEFOR SWAPPING - a:"+a+", b:"+b);
		temp=a;//temp=30
		a=b;//a=15
		b=temp;//b=30		
		System.out.println("\nAFTER SWAPPING - a:"+a+", b:"+b);
		}
}
